export const getStreakTitle = (streak: number) => {
    if (streak > 730) return "Titan";
    if (streak > 365) return "Legend";
    if (streak > 100) return "Grand Master";
    if (streak > 30) return "Master";
    if (streak > 7) return "Apprentice";
    return "Novice";
};

